beforeSaveCustomScript:
ifThen(
	useCase.priceList == 'A',
	listPrice = product.priceA
);
ifThen(
	useCase.priceList == 'B',
	listPrice = product.priceB
);
ifThen(
	useCase.priceList == 'C',
	listPrice = product.priceC
);
ifThen(
	useCase.priceList == 'E',
	listPrice = product.priceE
);
ifThen(
	useCase.priceList == 'R',
	listPrice = product.rentPrice
);
ifThen(
	useCase.priceList == 'Skoda',
	listPrice = product.priceSkoda
);
unitPrice = (listPrice - listPrice*discount/100);
amount = unitPrice*quantity;
ifThen(
    discount < 0,
    discount = 0
)

ifThenElse(
    product.categoryName != 'SERVICE', 
    amount = number\ceil(amount),
    amount = amount
);
ifThen(
    quantity == 1,
    quantity == 1
    )
